#!/bin/bash

[ "$1" = "del" ] && OPCODE=delroute || OPCODE=addroute
[ `id -u` -ne 0 ] && SUDOSUDO=sudo

GWADDR=10.0.0.1
DEVNAME=wlp1s0

# Fell asleep at the keyboard ;-)
#

routeutil ()
{
    ARG_OPCODE=$1
    ARG_NETWORK_ADDR=$2
    STATIC="-static"
    CMD="ip route $ARG_OPCODE $ARG_NETWORK_ADDR via $GWADDR dev $DEVNAME $STATIC"
    echo $SUDOSUDO $CMD
    $SUDOSUDO $CMD
}

addroute ()
{
    routeutil add $1
}

delroute ()
{
    routeutil del $1
}

